property.code:
@panelTitleColor: $request.panelTitleColor;
@panelTitleBgColor: $request.panelTitleBgColor;
@panelBgColor: $request.panelBgColor;
@panelBorderStyle: $request.panelBorderStyle;
@panelBorderColor: $request.panelBorderColor;
@panelBorderWidth: $request.panelBorderWidth;
.panelMacro {
  border-radius: 4px;
  background-color: @panelBgColor;
  border: @panelBorderStyle @panelBorderColor @panelBorderWidth;
}
.panelMacroTitle h1 {
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  border-bottom: 1px solid @panelTitleBgColor;
  color: @panelTitleColor;
  background-color: @panelTitleBgColor;
}
.panelMacroTitle h1,
.panelMacroContent {
  margin: 0;
  padding: 10px 15px 5px;
}
